import React, { Component } from 'react'
import { Modal, Form, Button, Col, Row, Input, Select, DatePicker } from 'antd';
import { PlusOutlined, DeleteOutlined } from '@ant-design/icons'
import { Card, Rate } from 'antd';
const products = [{
    name: 'camera',
    rate: 4,
    quality: 3
}, {
    name: 'mobile',
    rate: 1,
    quality: 2
}, {
    name: 'laptop',
    rate: 2,
    quality: 1
}, {
    name: 'bike',
    rate: 5,
    quality: 3
}, {
    name: 'guitar',
    rate: 3,
    quality: 3
}];
export default class ProductList extends Component {
    constructor(props) {
        this.showModal = this.bind(this)
    }


    state = {
        ModalText: 'Content of the modal',
        visible: false,
        confirmLoading: false,
    };

    showModal = () => {

        this.setState({
            visible: true,
        });
    };

    handleOk = () => {
        this.setState({
            ModalText: 'The modal will be closed after two seconds',
            confirmLoading: true,
        });
        setTimeout(() => {
            this.setState({
                visible: false,
                confirmLoading: false,
            });
        }, 2000);
    };

    handleCancel = () => {
        console.log('Clicked cancel button');
        this.setState({
            visible: false,
        });
    };
    render() {
        const { visible, confirmLoading, ModalText } = this.state;
        return (
            <>
                <div style={{ margin: '10px' }}>
                    <Button type="primary" onClick={this.showDrawer}>
                        <PlusOutlined /> New Product
        </Button>
                </div>
                <div>

                    <Card title="Product List">

                        {products.map(prd => {
                            return <Card.Grid >
                                <p>
                                    <span>Product name -</span> <span>{prd.name}</span>
                                </p>
                                <p>
                                    <span>Product Rate -</span> <span><Rate disabled defaultValue={prd.rate} /></span>
                                </p>
                                <p>
                                    <span>Product Quality -</span><span>  {prd.quality}</span>
                                </p>
                                <Button type="danger" onClick={this.showModal}>
                                    <DeleteOutlined />
                                </Button>
                            </Card.Grid>
                        })}
                    </Card>

                </div >
                <Modal
                    title="Title"
                    visible={visible}
                    onOk={this.handleOk}
                    confirmLoading={confirmLoading}
                    onCancel={this.handleCancel}
                >
                    <p>{ModalText}</p>
                </Modal>
            </>
        )
    }
}
